<script type="text/javascript">videojs.options.flash.swf = "<?php echo plugins_url()."/wizscriber-video-scribing-banner-ads/wizScribeSchell/" ?>player/video-js.swf"function onclickplay(){	wizSriberPlayer.play();}function startPlay(){	wizSriberPlayer.play();	}var time = parseInt("<?php echo $whentoappear; ?>") * 1000;function buttonclickstartprev(){setTimeout(function() {startPlay() }, time);jQuery('#wizScribe-wrapper').show();}jQuery(document).ready(function(){jQuery(function() {jQuery('#wizScribe-wrapper').hover(function() {     jQuery('#controls-hand').show(); 	}, function() {     jQuery('#controls-hand').hide(); 	});}); jQuery('#hand-close').click(function(){ 	wizSriberPlayer.dispose();	jQuery('#wizScribe-wrapper').hide();  });  jQuery('#hand-mute').click(function(){ 	wizSriberPlayer.muted(true);	  });  jQuery('#hand-play').click(function(){ 	wizSriberPlayer.play();	  });});</script><div id = "wizScribe-wrapper"><div id = "firsttext" ><?php echo $firsttext; ?></div><div id = "secondtext" ><?php echo $secondtext; ?></div><div id = "finaltexttop" ><?php echo $finaltexttop; ?></div><img src="<?php echo plugins_url()."/wizscriber-video-scribing-banner-ads/" ?>img/Paper.png" alt="Paper" id = "paper" /><div id = "finaltextbottom" ><?php if($actiononclick == 1){echo '<a id = "finaltextbottoma" href "'.$actiononclickurl.'">';} echo $finaltextbottom; if($actiononclick == 1){echo '</a>'; }?></div><img src="<?php echo plugins_url()."/wizscriber-video-scribing-banner-ads/wizScribeSchell/" ?>img/coverimg.png" alt="Call Now" id = "covertxtshow" /><div style = "border:none;width:384px;background:transparent;"><div id = "controls-hand"><div id = "crtwrp"><img src="<?php echo plugins_url()."/wizscriber-video-scribing-banner-ads/" ?>img/speaker-mute.png" alt="Logo" id = "hand-mute" /><img src="<?php echo plugins_url()."/wizscriber-video-scribing-banner-ads/" ?>img/close.png" alt="Logo" id = "hand-close"/><img src="<?php echo plugins_url()."/wizscriber-video-scribing-banner-ads/" ?>img/play-icon.png" alt="Play" id = "hand-play" /></div><a href="http://www.wizmotions.com/" id = "hand-logo-a"><img src="<?php echo plugins_url()."/wizscriber-video-scribing-banner-ads/" ?>img/logo.png" alt="Logo" id = "hand-logo"/></a></div><video id="wizScriber" style = "z-index:-1;" width="384" height="320" data-setup='{"example_option":true}'> <source src="<?php echo plugins_url()."/wizscriber-video-scribing-banner-ads/wizScribeSchell/" ?>Hand-knocking_v4.flv" type = "video/x-flv" /></video></div></div><script>var wizSriberPlayer = videojs('wizScriber');wizSriberPlayer.on("ended", function(){     (document).getElementById("covertxtshow").style.display = "block";	(document).getElementById("finaltexttop").style.display = "block";	(document).getElementById("finaltextbottom").style.display = "block";	(document).getElementById("paper").style.marginTop = '280px';	jQuery("#firsttext").hide().animate({width:'0px'}, 100);	jQuery("#secondtext").hide().animate({width:'0px'}, 100);	});function onTime(){	if (wizSriberPlayer.currentTime() > 7.5 && wizSriberPlayer.currentTime() < 8) {		 			jQuery("#firsttext").show().animate({width:'330px'}, 2900);			}		if (wizSriberPlayer.currentTime() > 5.8 && wizSriberPlayer.currentTime() < 7) {					jQuery("#paper").animate({marginTop:'-40px'}, 580);			}		if (wizSriberPlayer.currentTime() > 10.2 && wizSriberPlayer.currentTime() < 11) {					jQuery("#secondtext").show().animate({width:'310px'}, 3100);	}	}setInterval(function(){onTime()}, 100);wizSriberPlayer.on("play", function(){ 	(document).getElementById("covertxtshow").style.display = "none";	(document).getElementById("finaltexttop").style.display = "none";	(document).getElementById("finaltextbottom").style.display = "none";	});</script>	